public class GcZoomPanelDemo : Form
{
	private TextBox _childTextBox;
	private Button _childButton;
	private GcZoomPanel _gcZoomPanel;

	public GcZoomPanelDemo()
	{
		InitializeComponent();

		// Set the max zoom factor is 4.0
		_gcZoomPanel.MaxZoomFactor = 4f;

		// Set feedback area color
		_gcZoomPanel.FeedbackAreaBackColor = SystemColors.ControlDark;

		// If feedback area want to use image, you showld use FeedbackAreaBackgroundImage and 
		// FeedbackAreaBackgroundImageLayout property.
		//_gcZoomPanel.FeedbackAreaBackgroundImage = Image.FromFile(@"c:\image.jpg");
		//_gcZoomPanel.FeedbackAreaBackgroundImageLayout = ImageLayout.Stretch;
	}

	private void InitializeComponent()
	{
		_childTextBox = new TextBox();
		_childTextBox.Text = "This is TextBox";
		_childTextBox.Location = new Point(225, 150);
		_childTextBox.Size = new Size(150, 25);

		_childButton = new Button();
		_childButton.Text = "This is Button";
		_childButton.Location = new Point(225, 200);
		_childButton.Size = new Size(150, 25);

		_gcZoomPanel = new GcZoomPanel();
		_gcZoomPanel.Location = new Point(100, 100);
		_gcZoomPanel.Size = new Size(600, 400);
		_gcZoomPanel.BackColor = SystemColors.ControlDarkDark;

		_gcZoomPanel.Controls.Add(_childTextBox);
		_gcZoomPanel.Controls.Add(_childButton);

		this.Controls.Add(_gcZoomPanel);
	}
}

public class GcZoomMiscDemo : Form
{
	private CheckBox _allowPinchZoomCheckBox;
	private GcZoom _gcZoom1 = new GcZoom();

	public GcZoomMiscDemo()
	{
		InitializeComponent();

		_gcZoom1.Target = this;

		_allowPinchZoomCheckBox.CheckedChanged += _allowPinchZoomCheckBox_CheckedChanged;
	}

	void _allowPinchZoomCheckBox_CheckedChanged(object sender, EventArgs e)
	{
		_gcZoom1.AllowPinchZoom = _allowPinchZoomCheckBox.Checked;
	}

	private void InitializeComponent()
	{
		_allowPinchZoomCheckBox = new CheckBox();
		_allowPinchZoomCheckBox.Text = "AllowPinchZoom";
		_allowPinchZoomCheckBox.Location = new Point(20, 50);
		_allowPinchZoomCheckBox.Size = new Size(200, 20);
		this.Controls.Add(_allowPinchZoomCheckBox);
	}
}

public class GcZoomDemo : Form
{
	private TextBox _maxZoomFactorTextBox;
	private GcZoom _gcZoom1 = new GcZoom();

	public GcZoomDemo()
	{
		InitializeComponent();

		_gcZoom1.Target = this;

		_maxZoomFactorTextBox.TextChanged += maxZoomFactorTextBox_TextChanged;
	}

	void maxZoomFactorTextBox_TextChanged(object sender, EventArgs e)
	{
		string value = _maxZoomFactorTextBox.Text.Trim();

		try
		{
			float maxFactor = (float)Convert.ToDouble(value);
			_gcZoom1.MaxZoomFactor = maxFactor;
		}
		catch
		{
			_gcZoom1.MaxZoomFactor = 2f;
		}
	}

	private void InitializeComponent()
	{
		_maxZoomFactorTextBox = new TextBox();
		_maxZoomFactorTextBox.Location = new Point(160, 80);
		this.Controls.Add(_maxZoomFactorTextBox);
	}
}

public class GcZoomSnapPointDemo : Form
{
	private TextBox _zoomSnapTextBox;
	private GcZoom _gcZoom1 = new GcZoom();

	public GcZoomSnapPointDemo()
	{
		InitializeComponent();

		_gcZoom1.Target = this;

		// ZoomSnapPoints and ZoomSnapDistance property only take effect when  ZoomPreviewMode 
		// property value is ZoomPreviewMode.Bitmap or ZoomPreviewMode.AlternativeContent
		this._gcZoom1.ZoomPreviewMode = ZoomPreviewMode.Bitmap;

		_gcZoom1.ZoomSnapPoints.Add(1f);
		_gcZoom1.ZoomSnapPoints.Add(1.25f);
		_gcZoom1.ZoomSnapPoints.Add(1.5f);
		_gcZoom1.ZoomSnapPoints.Add(1.75f);
		_gcZoom1.ZoomSnapPoints.Add(2f);

		_gcZoom1.ZoomSnapDistance = 0.25f;

		_gcZoom1.ZoomFactorChanged += _gcZoom1_ZoomFactorChanged;
	}

	void _gcZoom1_ZoomFactorChanged(object sender, EventArgs e)
	{
		_zoomSnapTextBox.Text = _gcZoom1.ZoomFactor.ToString();
	}

	private void InitializeComponent()
	{
		_zoomSnapTextBox = new TextBox();
		_zoomSnapTextBox.Location = new Point(100, 100);
		this.Controls.Add(_zoomSnapTextBox);
	}
}

