 class GcZoomAnimationDemo : Form
 {
		private Button _scrollLeftBtn = new Button();
		private Button _scrollRightBtn = new Button();
		private Button _zoomInBtn = new Button();
		private Button _zoomOutBtn = new Button();
		private CheckBox _customizeDuringChB = new CheckBox();
		private GcZoom _gcZoom1 = new GcZoom();

		public GcZoomAnimationDemo()
		{
			InitializeComponent();

			_gcZoom1.Target = this;

			_scrollLeftBtn.Click += _scrollLeftBtn_Click;
			_scrollRightBtn.Click += _scrollRightBtn_Click;
			_zoomInBtn.Click += _zoomInBtn_Click;
			_zoomOutBtn.Click += _zoomOutBtn_Click;

			_gcZoom1.AnimationStarting += _gcZoom1_AnimationStarting;
			_gcZoom1.AnimationCompleted += _gcZoom1_AnimationCompleted;
		}

		void _zoomOutBtn_Click(object sender, EventArgs e)
		{
			_gcZoom1.SetZoomFactor(1, true);
		}

		void _zoomInBtn_Click(object sender, EventArgs e)
		{
			_gcZoom1.SetZoomFactor(2, true);
		}

		void _scrollRightBtn_Click(object sender, EventArgs e)
		{
			_gcZoom1.SetScrollLocation(new Point(-1000, 0), true);
		}

		void _scrollLeftBtn_Click(object sender, EventArgs e)
		{
			_gcZoom1.SetScrollLocation(new Point(0, 0), true);
		}

		void _gcZoom1_AnimationStarting(object sender, AnimationStartingEventArgs e)
		{
			if (_customizeDuringChB.Checked)
			{
				e.Duration = e.Duration * 4; // Make the animation slower.
			 	// e.Cancel = true;  // If it is necessary, cancel the animation by this code.
		 	}
			Console.WriteLine("+++ _gcZoom1_AnimationStarting +++");
			Console.WriteLine("CurrentScrollLocation:   " + e.CurrentScrollLocation.ToString());
			Console.WriteLine("CurrentZoomFactor:       " + e.CurrentZoomFactor.ToString());
			Console.WriteLine("TargetScrollLocation:    " + e.TargetScrollLocation.ToString());
			Console.WriteLine("TargetZoomFactor         " + e.TargetZoomFactor.ToString());
		}

		void _gcZoom1_AnimationCompleted(object sender, EventArgs e)
		{
			Console.WriteLine("+++ _gcZoom1_AnimationCompleted +++");
		}

		private void InitializeComponent()
		{
			this.Size = new Size(300, 600);

			this._scrollLeftBtn.Location = new System.Drawing.Point(12, 50);
			this._scrollLeftBtn.Size = new System.Drawing.Size(260, 30);
			this._scrollLeftBtn.Text = "Scroll Left";

			this._scrollRightBtn.Location = new System.Drawing.Point(12, 100);
			this._scrollRightBtn.Size = new System.Drawing.Size(260, 30);
			this._scrollRightBtn.Text = "Scroll Right";

			this._zoomInBtn.Location = new System.Drawing.Point(12, 150);
			this._zoomInBtn.Size = new System.Drawing.Size(260, 30);
			this._zoomInBtn.Text = "Zoom In";

			this._zoomOutBtn.Location = new System.Drawing.Point(12, 200);
			this._zoomOutBtn.Size = new System.Drawing.Size(260, 30);
			this._zoomOutBtn.Text = "Zoom Out";

			this._customizeDuringChB.Location = new System.Drawing.Point(12, 22);
			this._customizeDuringChB.Size = new System.Drawing.Size(183, 17);
			this._customizeDuringChB.Text = "Customize animtation during Time";

			this.Controls.Add(this._customizeDuringChB);
			this.Controls.Add(this._zoomOutBtn);
			this.Controls.Add(this._scrollRightBtn);
			this.Controls.Add(this._zoomInBtn);
			this.Controls.Add(this._scrollLeftBtn);
		}
	}

public class UserControlZoomPolicyDemo : Form
{
	private CheckBox _zoomItemBoundsCheckBox;
	private MyControl _myControl;
	private MyControlZoomPolicy _myControlZoomPolicy = new MyControlZoomPolicy();
	private GcZoom _gcZoom1 = new GcZoom();

	public UserControlZoomPolicyDemo()
	{
		InitializeComponent();

		_gcZoom1.Target = this;

		// Add MyControl's ZoomPolicy object to GcZoom
		_gcZoom1.ZoomPolicies.Add(_myControlZoomPolicy);

		_zoomItemBoundsCheckBox.CheckedChanged += _zoomItemBoundsCheckBox_CheckedChanged;
	}

	void _zoomItemBoundsCheckBox_CheckedChanged(object sender, EventArgs e)
	{
		_gcZoom1.ZoomFactor = 1f;
		_myControlZoomPolicy.ZoomItemBounds = _zoomItemBoundsCheckBox.Checked;
	}

	private void InitializeComponent()
	{
		_zoomItemBoundsCheckBox = new CheckBox();
		_zoomItemBoundsCheckBox.Text = "ZoomItemBounds";
		_zoomItemBoundsCheckBox.Location = new Point(20, 20);
		_zoomItemBoundsCheckBox.Size = new Size(120, 20);
		_zoomItemBoundsCheckBox.Checked = true;

		_myControl = new MyControl();
		_myControl.Location = new Point(200, 20);

		this.Controls.Add(_zoomItemBoundsCheckBox);
		this.Controls.Add(_myControl);
		this.Size = new Size(600, 400);
	}
}

// Implements a zoom policy for MyControl
internal class MyControlZoomPolicy : ZoomPolicy
{
	private bool _zoomItemBounds = true;

	public MyControlZoomPolicy()
	{
	}

	public bool ZoomItemBounds
	{
		get { return _zoomItemBounds; }
		set { _zoomItemBounds = value; }
	}

	// Gets the type indicates which control type can use this policy.
	public override Type TargetType
	{
		get { return typeof(MyControl); }
	}

	// Zoom the control bounds
	public override void ZoomBounds(Control control, ZoomBoundsInfo infos)
	{
		if (ZoomItemBounds)
		{
			MyControl myControl = control as MyControl;
			myControl.ColumnWidth = infos.Zoom(myControl.ColumnWidth);
			myControl.RowHeight = infos.Zoom(myControl.RowHeight);
		}

		base.ZoomBounds(control, infos);
	}

	// Zoom the control font
	public override void ZoomFont(Control control, ZoomFontInfo infos)
	{
		base.ZoomFont(control, infos);
	}
}

// MyControl component
internal class MyControl : UserControl
{
	private int _columnWidth;
	private int _rowHeight;
	private Brush[,] _brushes = {
	{Brushes.AliceBlue, Brushes.Aqua},
	{Brushes.Beige, Brushes.BlanchedAlmond},
	{Brushes.BurlyWood, Brushes.Chocolate},
	{Brushes.Crimson, Brushes.DarkGoldenrod},
	{Brushes.DarkMagenta, Brushes.DarkRed},
	{Brushes.DarkSlateGray, Brushes.DeepSkyBlue},
	{Brushes.Firebrick, Brushes.GhostWhite}
};

	public MyControl()
	{
		InitializeComponent();

		this.ColumnWidth = 40;
		this.RowHeight = 40;
	}

	public int ColumnWidth
	{
		get { return _columnWidth; }
		set { _columnWidth = value; }
	}

	public int RowHeight
	{
		get { return _rowHeight; }
		set { _rowHeight = value; }
	}

	private void InitializeComponent()
	{
		this.BackColor = SystemColors.Control;
		this.Size = new Size(80, 280);
	}

	protected override void OnPaint(PaintEventArgs e)
	{
		Draw(e.Graphics);
		base.OnPaint(e);
	}

	private void Draw(Graphics gObj)
	{
		gObj.DrawRectangle(SystemPens.ControlDark, 0, 0, this.Width - 1, this.Height - 1);

		for (int i = 0; i < 2; ++i)
		{
			for (int j = 0; j < 7; ++j)
			{
				Rectangle rect = new Rectangle(i * _columnWidth, j * _rowHeight, _columnWidth, _rowHeight);
				rect.Inflate(-6, -6);

				gObj.DrawRectangle(SystemPens.ControlDark, rect);
				gObj.DrawLine(SystemPens.ControlDarkDark, rect.Left, rect.Top + 1, rect.Right, rect.Top + 1);
				gObj.DrawLine(SystemPens.ControlDarkDark, rect.Left + 1, rect.Top, rect.Left + 1, rect.Bottom);
				gObj.DrawLine(SystemPens.ControlLightLight, rect.Right + 1, rect.Top, rect.Right + 1, rect.Bottom);
				gObj.DrawLine(SystemPens.ControlLightLight, rect.Left, rect.Bottom + 1, rect.Right, rect.Bottom + 1);

				rect.Inflate(-1, -1);
				rect.Offset(1, 1);
				gObj.FillRectangle(_brushes[j, i], rect);
			}
		}
	}
}

