public class ControlBarDemo : Form
{
	private Button _controlBarButton;
	private MyUserControl _userControl = new MyUserControl();
	private GcZoom _gcZoom1 = new GcZoom();

	public ControlBarDemo()
	{
		InitializeComponent();

		_gcZoom1.Target = this;

		// Create ControlBar
		AddControlBar();
	}

	private void AddControlBar()
	{
		ControlBar controlBar = new ControlBar();

		// Content should is a Type of UserControl.
		controlBar.Content = _userControl.GetType();

		controlBar.PositionMode = PositionMode.Floating;

		// Only take effect when Position Mode is Dock.
		// controlBar.DockPosition = ContentAlignment.MiddleCenter;
		// controlBar.Margin = new System.Windows.Forms.Padding();

		// Only take effect when Position is Fix or Flowing.
		controlBar.Position = new Point(20, 20);

		controlBar.Opacity = 1d;
		controlBar.TransparentBackground = true;

		_gcZoom1.ControlBars.Add(controlBar);
	}

	private void InitializeComponent()
	{
		_controlBarButton = new Button();
		_controlBarButton.Text = "This is a Button";
		_controlBarButton.Location = new Point(100, 100);
		_controlBarButton.Size = new Size(120, 30);
		this.Controls.Add(_controlBarButton);
	}
}

public class MyUserControl : UserControl
{
	private GcZoomCommandProvider commands = new GcZoomCommandProvider();
	private Button _zoomInButton;
	private Button _zoomOutButton;
	private Button _getButton;

	public MyUserControl()
	{
		InitializeComponent();

		commands.Target = this;

		commands.SetZoomCommands(_zoomInButton, ZoomCommands.ZoomIn);
		Console.WriteLine(commands.GetZoomCommands(_zoomInButton).ToString());

		commands.SetZoomCommands(_zoomOutButton, ZoomCommands.ZoomOut);
		Console.WriteLine(commands.GetZoomCommands(_zoomOutButton).ToString());

		_getButton.Click += _getButton_Click;
	}

	void _getButton_Click(object sender, EventArgs e)
	{
		Console.WriteLine(commands.OwnerForm.ToString());
		Console.WriteLine(commands.OwnerGcZoom.ToString());
	}

	private void InitializeComponent()
	{
		_zoomInButton = new Button();
		_zoomInButton.Text = "+";
		_zoomInButton.Size = new Size(40, 40);
		_zoomInButton.Location = new Point(10, 10);

		_zoomOutButton = new Button();
		_zoomOutButton.Text = "_";
		_zoomOutButton.Size = new Size(40, 40);
		_zoomOutButton.Location = new Point(60, 10);

		_getButton = new Button();
		_getButton.Text = "Get";
		_getButton.Size = new Size(40, 40);
		_getButton.Location = new Point(110, 10);

		this.Controls.Add(_zoomInButton);
		this.Controls.Add(_zoomOutButton);
		this.Controls.Add(_getButton);
	}
}

public class DataGridViewZoomPolicyDemo : Form
{
	private DataGridView _dataGridView;
	private GcZoom _gcZoom1 = new GcZoom();
	private DataGridViewZoomPolicy _zoomPolicy = new DataGridViewZoomPolicy();

	public DataGridViewZoomPolicyDemo()
	{
		InitializeComponent();

		// DataGridView control should to change default font
		_dataGridView.ColumnHeadersDefaultCellStyle.Font = new Font(this.Font.FontFamily, this.Font.Size);
		_dataGridView.RowsDefaultCellStyle.Font = new Font(this.Font.FontFamily, this.Font.Size);

		_gcZoom1.Target = this;

		 // Set DataGridView's ZoomPolicy object
		_gcZoom1.ZoomPolicies.Add(_zoomPolicy);
	}

	private void InitializeComponent()
	{
		_dataGridView = new DataGridView();
		_dataGridView.Location = new Point(20, 20);
		_dataGridView.Size = new Size(400, 500);

		DataGridViewColumn column1 = new DataGridViewColumn();
		column1.CellTemplate = new DataGridViewTextBoxCell();
		column1.HeaderText = "Column 1";
		_dataGridView.Columns.Add(column1);

		DataGridViewColumn column2 = new DataGridViewColumn();
		column2.CellTemplate = new DataGridViewTextBoxCell();
		column2.HeaderText = "Column 2";
		_dataGridView.Columns.Add(column2);

		DataGridViewColumn column3 = new DataGridViewColumn();
		column3.CellTemplate = new DataGridViewTextBoxCell();
		column3.HeaderText = "Column 3";
		_dataGridView.Columns.Add(column3);

		this.Controls.Add(_dataGridView);
	}
}

public class DataGridViewZoomPolicyDemo : Form
{
	private DataGridView _dataGridView;
	private ListBox _defaultZoomPolicyListBox;
	private GcZoom _gcZoom1 = new GcZoom();
	private DataGridViewZoomPolicy _zoomPolicy = new DataGridViewZoomPolicy();

	public DataGridViewZoomPolicyDemo()
	{
		InitializeComponent();

		// DataGridView control should to change default font
		_dataGridView.ColumnHeadersDefaultCellStyle.Font = new Font(this.Font.FontFamily, this.Font.Size);
		_dataGridView.RowsDefaultCellStyle.Font = new Font(this.Font.FontFamily, this.Font.Size);

		_gcZoom1.Target = this;

		// Set DataGridView's ZoomPolicy object
		_gcZoom1.ZoomPolicies.Add(_zoomPolicy);

		GetDefaultZoomPolicy();
	}

	void GetDefaultZoomPolicy()
	{
		// Get GcZoom component build in zoompolicy of all
		foreach (ZoomPolicy item in _gcZoom1.DefaultZoomPolicies)
		{
			_defaultZoomPolicyListBox.Items.Add(item.ToString());
		}
	}

	private void InitializeComponent()
	{
		_dataGridView = new DataGridView();
		_dataGridView.Location = new Point(20, 20);
		_dataGridView.Size = new Size(400, 500);

		DataGridViewColumn column1 = new DataGridViewColumn();
		column1.CellTemplate = new DataGridViewTextBoxCell();
		column1.HeaderText = "Column 1";
		_dataGridView.Columns.Add(column1);

		DataGridViewColumn column2 = new DataGridViewColumn();
		column2.CellTemplate = new DataGridViewTextBoxCell();
		column2.HeaderText = "Column 2";
		_dataGridView.Columns.Add(column2);

		DataGridViewColumn column3 = new DataGridViewColumn();
		column3.CellTemplate = new DataGridViewTextBoxCell();
		column3.HeaderText = "Column 3";
		_dataGridView.Columns.Add(column3);

		_defaultZoomPolicyListBox = new ListBox();
		_defaultZoomPolicyListBox.Size = new Size(300, 200);
		_defaultZoomPolicyListBox.Location = new Point(450, 170);

		this.Controls.Add(_dataGridView);
		this.Controls.Add(_defaultZoomPolicyListBox);
	}
}

public class GcZoomControlsDemo : Form
{
	private GcZoom _gcZoom1 = new GcZoom();
	private ComboBox _innerPanelLayoutModeComboBox;

	public GcZoomControlsDemo()
	{
		InitializeComponent();

		_gcZoom1.Target = this;

		_innerPanelLayoutModeComboBox.TextChanged += _innerPanelLayoutModeComboBox_TextChanged;
	}

	void _innerPanelLayoutModeComboBox_TextChanged(object sender, EventArgs e)
	{
		if (_innerPanelLayoutModeComboBox.Text == "None")
		{
			_gcZoom1.InnerPanelLayoutMode = InnerPanelLayoutMode.None;
		}
		else if (_innerPanelLayoutModeComboBox.Text == "MiddleCenter")
		{
			_gcZoom1.InnerPanelLayoutMode = InnerPanelLayoutMode.MiddleCenter;
		}
	}

	private void InitializeComponent()
	{
		_innerPanelLayoutModeComboBox = new System.Windows.Forms.ComboBox();
		_innerPanelLayoutModeComboBox.Items.AddRange(new object[] { "None", "MiddleCenter" });
		_innerPanelLayoutModeComboBox.Location = new Point(120, 20);
		this.Controls.Add(_innerPanelLayoutModeComboBox);
	}
}

public class GcApplicationZoomDemo : Form
{
	private Button _showFormButton;
	private GcApplicationZoom _gcApplicationZoom;

	public GcApplicationZoomDemo()
	{
		InitializeComponent();

		_showFormButton.Click += _showFormButton_Click;
		_gcApplicationZoom.GcZoomAttached += _gcApplicationZoom_GcZoomAttached;
		_gcApplicationZoom.GcZoomDetached += _gcApplicationZoom_GcZoomDetached;
	}

	void _gcApplicationZoom_GcZoomAttached(object sender, GcZoomAttachedEventArgs e)
	{
		// If you want specific GcZoom's setting different from GcApplicationZoom, You can change it in
		// GcZoomAttaching or GcZoomAttached event.
		e.GcZoom.MaxZoomFactor = 4f;

		if (e.Form is ZoomForm)
		{
			e.GcZoom.ZoomFactorChanged += (e.Form as ZoomForm).ZoomFactorChangeProc;
		}
	}

	void _gcApplicationZoom_GcZoomDetached(object sender, GcZoomDetachedEventArgs e)
	{
		if (e.Form is ZoomForm)
		{
			e.GcZoom.ZoomFactorChanged -= (e.Form as ZoomForm).ZoomFactorChangeProc;
		}
	}

	void _showFormButton_Click(object sender, EventArgs e)
	{
		Form form = new ZoomForm();
		form.Show();
	}

	private void InitializeComponent()
	{
		_gcApplicationZoom = new GcApplicationZoom();
		_showFormButton = new Button();
		_showFormButton.Text = "Click to open ZoomForm";
		_showFormButton.Location = new Point(20, 20);
		_showFormButton.Size = new Size(200, 40);
		this.Controls.Add(_showFormButton);
	}
}

internal class ZoomForm : Form 
{
	public ZoomForm()
	{
	}

	public void ZoomFactorChangeProc(object sender, EventArgs e)
	{
		Console.WriteLine(GcApplicationZoom.GetGcZoom(this).ZoomFactor.ToString());
	}
}

 public class GcApplicationZoomDemo : Form
 {
		private Button _showForm2Button;
		private GcApplicationZoom _gcApplicationZoom;

		public GcApplicationZoomDemo()
		{
			InitializeComponent();

			_showForm2Button.Click += _showForm2Button_Click;
			_gcApplicationZoom.GcZoomAttaching += _gcApplicationZoom_GcZoomAttaching;
		}

		void _gcApplicationZoom_GcZoomAttaching(object sender, GcZoomAttachingEventArgs e)
		{
			// May be some forms should not be zoom.
			if (e.Form is NormalForm)
			{
				e.Cancel = true;
			}
			else
			{
				e.GcZoom.AutoShowControl = true;
			}
		}

		void _showForm2Button_Click(object sender, EventArgs e)
		{
			Form form = new NormalForm();
			form.Show();
		}

		private void InitializeComponent()
		{
			_gcApplicationZoom = new GcApplicationZoom();

			_showForm2Button = new Button();
			_showForm2Button.Text = "Click to open NormalForm";
			_showForm2Button.Location = new Point(20, 80);
			_showForm2Button.Size = new Size(200, 40);
			this.Controls.Add(_showForm2Button);
		}
 }

 internal class NormalForm : Form
 {
		private Button _showFormButton;

		public NormalForm()
		{
			InitializeComponent();
			_showFormButton.Click += _showFormButton_Click;
		}

		void _showFormButton_Click(object sender, EventArgs e)
		{
			Form form = new NormalForm();
			form.Show();
		}

		private void InitializeComponent()
		{
			_showFormButton = new Button();
			_showFormButton.Text = "Click to open a new form";
			_showFormButton.Location = new Point(20, 20);
			_showFormButton.Size = new Size(200, 40);

			this.Controls.Add(_showFormButton);
			this.Text = "NormalForm";
		}
 }

