	public class GcZoomEventDemo : Form
	{
		private GcZoom _gcZoom1 = new GcZoom();

		public GcZoomEventDemo()
		{
			_gcZoom1.Target = this;
			_gcZoom1.AutoShowControl = true;
			_gcZoom1.ZoomFactorChanged += _gcZoom1_ZoomFactorChanged;
		}

		void _gcZoom1_ZoomFactorChanged(object sender, EventArgs e)
		{
			Console.WriteLine(_gcZoom1.ZoomFactor.ToString());
		}
 }

public class GcZoomEventDemo : Form
{
	private GcZoom _gcZoom1 = new GcZoom();

	public GcZoomEventDemo()
	{
		_gcZoom1.Target = this;
		_gcZoom1.AutoShowControl = true;
		_gcZoom1.ControlBoundsZooming += _gcZoom1_ControlBoundsZooming;
	}

	void _gcZoom1_ControlBoundsZooming(object sender, ControlBoundsZoomingEventArgs e)
	{
		ZoomBoundsInfo info = e.ZoomBoundsInfo;

		Console.WriteLine(e.TargetControl.ToString());
		Console.WriteLine(info.CurrentFactor.ToString());
		Console.WriteLine(info.TargetFactor.ToString());

		// For complex control, user may want to change the control's inner element's size.
		// User can use ControlBoundsZooming to support complex control.
		if (e.TargetControl is TextBox)
		{
			// If set to true, default zoom logic for this control will be canceled.
			e.Handled = true;
		}

		// If target control has children controls and wan't to use default zoom logic for children 
		// controls, set e.ChildrenHandled property to true.
		// e.ChildrenHandled = true; 
	}
}

public class GcZoomEventDemo : Form
{
	private GcZoom _gcZoom1 = new GcZoom();

	public GcZoomEventDemo()
	{
		_gcZoom1.Target = this;
		_gcZoom1.AutoShowControl = true;
		_gcZoom1.ControlFontZooming += _gcZoom1_ControlFontZooming;
	}

	void _gcZoom1_ControlFontZooming(object sender, ControlFontZoomingEventArgs e)
	{
		ZoomFontInfo info = e.ZoomFontInfo;

		Console.WriteLine(e.TargetControl.ToString());
		Console.WriteLine(info.CurrentFontSize.ToString());

		// For complex control, user may want to change the control's inner element's font.
		// User can use ControlFontZooming to support complex control.
		if (e.TargetControl is TextBox)
		{
			e.TargetControl.Font = new Font(e.TargetControl.Font.FontFamily, e.ZoomFontInfo.CurrentFontSize);

			// If set to true, default zoom logic for this control will be canceled.
			e.Handled = true;
		}

		// If target control has children controls and wan't to use default zoom logic for children 
		// controls, set e.ChildrenHandled property to true.
		// e.ChildrenHandled = true; 
	}
}

public class GcZoomEventDemo : Form
{
	private GcZoom _gcZoom1 = new GcZoom();

	public GcZoomEventDemo()
	{
		_gcZoom1.Target = this;
		_gcZoom1.AutoShowControl = true;
		_gcZoom1.ManipulationStarting += _gcZoom1_ManipulationStarting;
	}

	void _gcZoom1_ManipulationStarting(object sender, ZoomManipulationStartingEventArgs e)
	{
		Console.WriteLine(e.TargetControl.ToString());
		Console.WriteLine(e.Position.ToString());

		if (e.TargetControl is TextBox)
		{
			// Pan gesture send to child control, Zoom gesture send to Form
			e.Mode = ZoomManipulationModes.Zoom;
		}
		else if (e.TargetControl is Button)
		{
			// Zoom gesture send to child control, Pan gesture send to Form
			e.Mode = ZoomManipulationModes.Pan;
		}
		else
		{
			// Pan and Zoom gesture send to Form
			e.Mode = ZoomManipulationModes.All;
		}
	}
}

public class GcZoomEventDemo : Form
{
	private GcZoom _gcZoom1 = new GcZoom();

	public GcZoomEventDemo()
	{
		_gcZoom1.Target = this;
		_gcZoom1.AutoShowControl = true;
		_gcZoom1.ControlAutoShowing += _gcZoom1_ControlAutoShowing;
	}

	void _gcZoom1_ControlAutoShowing(object sender, ControlAutoShowingEventArgs e)
	{
		Console.WriteLine(e.ShowingControl.ToString());
		Console.WriteLine(e.ShowingReason.ToString());

		// Do not auto scroll if a control's most part is shown
		if (_gcZoom1.GetDisplayRectangle(e.ShowingControl).Width > e.ShowingControl.Size.Width / 2)
		{
			e.Cancel = true;
		}
	}

}

class GcZoomAnimationDemo : Form
{
	private GcZoom _gcZoom1 = new GcZoom();

	public GcZoomAnimationDemo()
	{
		_gcZoom1.Target = this;
		_gcZoom1.AnimationStarting += _gcZoom1_AnimationStarting;
	}

	void _gcZoom1_AnimationStarting(object sender, AnimationStartingEventArgs e)
	{
		if (_customizeDuringChB.Checked)
		{
			e.Duration = e.Duration * 4; // Make the animation slower.

			// e.Cancel = true;  // If it is necessary, cancel the animation by this code.
		}
		Console.WriteLine("+++ _gcZoom1_AnimationStarting +++");
		Console.WriteLine("CurrentScrollLocation:   " + e.CurrentScrollLocation.ToString());
		Console.WriteLine("CurrentZoomFactor:       " + e.CurrentZoomFactor.ToString());
		Console.WriteLine("TargetScrollLocation:    " + e.TargetScrollLocation.ToString());
		Console.WriteLine("TargetZoomFactor         " + e.TargetZoomFactor.ToString());
	}
}

